    //   }
    // })
  },
    10)
  return (
    <div className="homepage_tabs">
      <div className="tab__item active">FLOWER</div>
      <div className="tab__item">TEDDY</div>
      <div className="tab__item">GIFTBOX</div>
      <div className="tab__item">LOVE</div>
    </div>

  )
}

export default Tab
